mapper.update:
update members
set member_grade = #{memberGrade}
where member_no = #{memberNo}

mapper.select:
select member_no as memberNo, member_id as memberId, member_pw as memberPw, member_name as memberName, member_phone as memberPhone, member_email as memberEmail, member_addr as memberAddr, member_grade as memberGrade, member_birth as memberBirth, enroll_date as memberDate, nickName as nickName, member_state as memberState, member_img as memberImg, favorite as favorite, kakaoLogin as kakaoLogin from members where
member_id=#{memberId}
kakaoLogin=#{kakaoLogin}
member_name=#{memberName}
nickname=#{nickName}
and member_pw=#{memberPw}
and member_phone=#{memberPhone}
and member_email=#{memberEmail}

mapper.update:
update
members
set
member_email=#{memberEmail}, member_phone=#{memberPhone}, favorite=#{favorite}, nickName = #{nickName}, member_birth=#{memberBirth}
where

member_id=#{memberId}


kakaoLogin=#{kakaoLogin}

update
members
set
member_pw=#{memberPw}
where
member_email=#{memberEmail}
